(1, 'Daniel', '$2y$10$q09PS7m49fv0CjwEHFh7WehAbuD2wJrUjd0iKwB8zu7tevP41sfa.', 'daniel@rouaix.com', '2026-01-06 08:41:02', NULL),
(4, 'Greg', '$2y$10$CXOldmo3N21sSLXMIvMbOuUQ/ObFvsbm3aRdjUYZh8CcHTedgg.i6', 'rougeulgregory@yahoo.fr', '2026-01-20 14:29:58', NULL);
